import React, { useState } from "react";
import FooterComponent from "../components/footer";
import Header from "../components/header";

export default function Contact(props) {
  const [NameError, setNameError] = useState(false);
  const [EmailError, setEmailError] = useState(false);
  const [MessageError, setMessageError] = useState(false);
  const emailPattern = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/;
  const namePattern = /^(([A-Za-z]+[\-\']?)*([A-Za-z]+)?\s)+([A-Za-z]+[\-\']?)*([A-Za-z]+)?$/;
  const messagePattern = /^(([A-Za-z]+[\-\']?)*([A-Za-z]+)?\s)+([A-Za-z]+[\-\']?)*([A-Za-z]+)?$/;

  const nameCheck = (value) => {
    if (!value === "") {
      if (!namePattern.test(value)) {
        return true;
      } else {
        return false;
      }
    } else {
      return true;
    }
  };
  const emailCheck = (value) => {
    if (!value === "") {
      if (!emailPattern.test(value)) {
        return true;
      } else {
        return false;
      }
    } else {
      return true;
    }
  };
  const messageCheck = (value) => {
    if (!value === "") {
      if (!messagePattern.test(value)) {
        return true;
      } else {
        return false;
      }
    } else {
      return true;
    }
  };
  const handleSubmit = (input) => {
    input.preventDefault();
    let name = input.target.name;
    let value = input.target.value;

    switch (name) {
      case "clientName":
        if (!nameCheck(value)) {
          setNameError(false);
        } else {
          setNameError(true);
        }
        break;
      case "email":
        if (!emailCheck(value)) {
          setEmailError(false);
        } else {
          setEmailError(true);
        }
        break;
      case "message":
        if (!messageCheck(value)) {
          setMessageError(false);
        } else {
          setMessageError(true);
        }
        break;

      default:
        break;
    }
  };
  return (
    <>
      <div className="conCont">
        <Header />
        <div className="row">
          <div className="col-md-4 col-md-offset-4 col-xs-8 col-xs-offset-2 conCont__contForm">
            <h2>Contact form</h2>
            <form
              method="POST"
              action="http://localhost:8080/HOLIDAZE/client/src/server/contact-success.php"
            >
              <div className="row col-md-12 conCont__contForm__name">
                <div className="col-md-3 col-xs-3">
                  <label for="clientName">Fullname</label>
                </div>
                <div className="col-md-9 col-xs-9">
                  <input
                    type="text"
                    onChange={handleSubmit}
                    name="clientName"
                    id="name"
                  />
                  <p className={NameError ? "error" : "error__hide"}>
                    Full Name Required
                  </p>
                </div>
              </div>
              <div className="row col-md-12 conCont__contForm__email">
                <div className="col-md-3 col-xs-3">
                  <label for="email">Email</label>
                </div>
                <div className="col-md-9 col-xs-9">
                  <input
                    type="text"
                    onChange={handleSubmit}
                    name="email"
                    id="email"
                  />
                  <p className={EmailError ? "error" : "error__hide"}>
                    Email Invalid
                  </p>
                </div>
              </div>
              <div className="row col-md-12 conCont__contForm__message">
                <div className="col-md-4 col-xs-4">
                  <label for="message">Message</label>
                </div>
                <div className="col-md-8 col-xs-8">
                  <textarea
                    name="message"
                    onChange={handleSubmit}
                    id="message"
                    rows="3"
                    cols="20"
                  ></textarea>
                  <p className={MessageError ? "error" : "error__hide"}>
                    Invalid message
                  </p>
                </div>
              </div>
              <div className="row col-md-4 col-md-offset-4 col-xs-4 col-xs-offset-4 conCont__contForm__submit">
                <button
                  className={
                    (NameError || EmailError || MessageError)
                      ? "disabled" 
                      : "enable"
                  }
                >
                  Submit
                </button>              
              </div>
            </form>
          </div>
        </div>
        <FooterComponent />
      </div>
    </>
  );
}
