import React, { useState, useEffect } from 'react';
import FooterComponent from "../components/footer";
import Header from "../components/header";


export default function SignIn(props){
    const [userName,setUserName] = useState("");
    const [password,setPassword] = useState("");
    const [userNameError,setUserNameError] = useState(true);
    const [passwordError,setPasswordError] = useState(true);

    const handleChange = (input) => {
        let name = input.target.name;
        let value = input.target.value;
        switch (name) {
            case 'userName':
                (value === 'admin') ? setUserNameError(false) : setUserNameError(true) 
                break;
            case 'password':
                (value === 'admin123') ? setPasswordError (false) : setPasswordError(true)
                break;
            default:
        }
    }

    const handleSubmit = () => {
        (this.state.passwordError && this.state.passwordError === true) ? this.props.updateLoginStatus() : alert('Username or password is incorrect');
    }


    return(
     <>  
        <div className="signCont">
            <Header />
            <div className="row">
                <div className="col-md-4 col-md-offset-4 signCont__signForm">
                    <h2>Sign In</h2> 
                    <form onSubmit={this.handleSubmit}>
                        <div className="row col-md-12 signCont__signForm__name">
                            <div className="col-md-3"><label for="userName">Username</label></div>
                            <div className="col-md-9"><input type="text" onChange={this.handleChange} name="clientName" className="userName" id="userName"/></div>
                        </div>
                        <div className="row col-md-12 signCont__signForm__pass">
                            <div className="col-md-3"><label for="password">Password</label></div>
                            <div className="col-md-9"><input type="text" onChange={this.handleChange} name="password" className="password" id="password"/></div>
                        </div>
                        <div className="row col-md-4 col-md-offset-4 signCont__signForm__submit">
                            {/* <button>Submit</button> */}
                            <input type='submit' className='btn' />
                        </div>     
                    </form> 
                </div> 
            </div>
            <FooterComponent />  
        </div> 
     </>
    )
}



