import React,{useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import FooterComponent from "../components/footer";
import hotel from "../images/b&b-2.jpg";
import Estresp from "../server/establishments.json";


export default function CardSpecific(props){


    // const filterPara = props.match.params.id -1;
    const filterPara = props.match.params.id;


    return(
     <> 

        <div className="SpecCont">
            <div className="row SpecCont__searcharea col-md-8 col-md-offset-2">
                <div className="col-md-12 form-group">
                    <input className="form-control" type="text" placeholder="Search by name" id="dest"></input>
                    <button className="btn btn-primary">
                        <h4>Search</h4>
                    </button>
                </div>   
            </div> 

            <div className="row SpecCont__body">
                <div className="col-md-8 col-md-offset-2 ">
                {
         (filterPara != null)?
         Estresp.map((data)=>{
            if(data.id === filterPara){
                return(
                    <div className="card SpecCont__body__card">
                    <img className="card-img-top" src={hotel} alt="Card image cap"/>
                <div className="card-body">
                    <h1 className="card-title">{data.establishmentName}</h1>
                    <h2 className="card-text">{850} kr / Night</h2>
                    <div className="row col-md-10 col-md-offset-1">
                        <div className="row col-md-6 SpecCont__body__card__max">
                            <h3 className="card-text">Max guest : 18</h3>
                        </div>
                        <div className="row col-md-6 SpecCont__body__card__self">
                            <h3 className="card-text">Self Catering : Yes</h3>
                        </div>
                    </div>
                    <div className="row col-md-8 col-md-offset-2">
                        <h3>Description :</h3>
                        <h4>"Get ready for some amazing sunsets as you sip a cocktail and watch dolphins play in the harbour below."</h4>
                    </div>
                    <div className="col-md-2 col-md-offset-5 SpecCont__body__card__button"><Link to="/Enquiry"><button>Book now</button></Link></div>    
                </div>
            </div>    

                    );
            }
        })
         :
         <h2>no post found</h2>
     } 
                </div>                        
            </div>
            <FooterComponent />           
        </div>
     </>
    )
}
